import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-auction-card',
  templateUrl: './auction-card.component.html',
  styleUrls: ['./auction-card.component.css']
})
export class AuctionCardComponent {

  @Input() auction: any; // You can create Auction model for type-safety
  @Output() bidPlaced = new EventEmitter<number>();

  onBidClick() {
    this.bidPlaced.emit(this.auction.id);  // Send auction ID to parent
  }
}

<div class="auction-card">
  <h2>{{ auction.title }}</h2>
  <p>{{ auction.description }}</p>
  <p><strong>Starting Price:</strong> ₹{{ auction.price }}</p>
  <p><strong>Ends At:</strong> {{ auction.endsAt | date : "medium" }}</p>
  <button (click)="onBidClick()">Place Bid</button>
</div>
